plan.command:
for file in "$DOWNLOADS"/*; do
    mime=$(file --mime-type -b "$file")
    case "$mime" in
        image/*)   mv -n "$file" "$DOWNLOADS/Images/";;
        video/*)   mv -n "$file" "$DOWNLOADS/Videos/";;
        application/pdf|application/msword|application/vnd.*) mv -n "$file" "$DOWNLOADS/Documents/";;
        application/zip|application/x-tar|application/gzip|application/x-bzip2) mv -n "$file" "$DOWNLOADS/Archives/";;
        *)         mv -n "$file" "$DOWNLOADS/Others/";;
    esac
done

url="https://api.example.com/v1/status"; logfile=/tmp/api_test.log; curl -L -w "
Time_Total:%{time_total}
HTTP_Code:%{http_code}
" -o /dev/null $url > $logfile; echo "Test result saved to $logfile"
